import Header from './components/Header'
import Posts from './components/Posts'
import AddPost from './components/AddPost'
import { useState } from 'react'
import './App.css';

function App() {
  const [showAddPost, setShowAddPost] = useState(false)
  const [posts, setPosts] = useState( [
    {id: 1, 
    title: 'Walk dog',
    Price: 20,
    Description: 'Walk dog for $20 dollars per day',
    Contact: 7785225269,
    Image: '/Users/jinyifan/Desktop/friendly-neighbor/public/logo192.png'},
    {id: 2, 
      title: 'Calculus tutor for 3 years old kid',
      Price: 50,
      Description: 'Teach calculus and abstract algebra to my kid Johnny',
      Contact: 1101101110,
      Image: '/Users/jinyifan/Desktop/friendly-neighbor/public/logo192.png'},
    {id: 3, 
      title: 'Gardening',
      Price: 30,
      Description: 'My Garden',
      Contact: 9119119111,
      Image: '/Users/jinyifan/Desktop/friendly-neighbor/public/logo192.png'}

]
)
// Add post
const addPost = (post) => {
  const id = Math.floor(Math.random() * 10000) + 1
  const newPost = {id, ...post}
  setPosts([...posts, newPost])
}

// Delete post
const deleteTask = (id) => {
  setPosts(posts.filter((task)=>task.id!==id))
}
  return (
    <div className="container">
      <header className="App-header">
        <Header onAdd={() => setShowAddPost(!showAddPost)}
        showAddPost = {showAddPost}/>
        {showAddPost && <AddPost onAdd={addPost}/>}
        {posts.length > 0 ? 
        <Posts posts={posts} onDelete = {deleteTask}/> : 'No post to show'}
      </header>
    </div>
  );
}

export default App;
